async function openPopup(name,data={}){const html=$(await $.get(`${name}.html`));name=name.replace(/[^a-zA-Z]/g,"");let popup=$(`<div class='popup' id="${name}-popup">`);const popupContent=$(`<div class='popup-content'>`);for(let i=0;i<html.length;i++){popupContent.append(html[i])}popupContent.append(`<button class="close"><i class="fa fa-close"></i></button>`);const bg=$('<div class="close popup-bg"></div>');bg.on("click",()=>{closePopup(name)});popup.append(bg);popup.append(popupContent);popup.appendTo("body");setTimeout(()=>{popup.addClass("active");popup.find(".close").on("click",()=>{closePopup(name)})},100);return popup}function closePopup(name){const popup=$(`#${name}-popup.popup.active`);popup.removeClass("active");setTimeout(()=>{popup.remove()},300)}function alert(message,onclose=()=>{}){const name=`alert-${(new Date).getTime()}`;let popup=$(`<div id="${name}-popup" class='popup'>`);const popupContent=$(`<div class='popup-content'>`);popupContent.append(`<h1>Alert</h1>`);popupContent.append(`<p>${message}</p>`);popupContent.append(`<button class="close primary">Close</button>`);const bg=$('<div class="close popup-bg"></div>');popup.append(popupContent);popup.append(bg);popup.appendTo("body");setTimeout(()=>{popup.addClass("active");popup.find(".close").on("click",()=>{closePopup(name);onclose()})},100)}export{openPopup,closePopup,alert};