$(document).on("load",()=>{const toggles=$("toggle");$(`[toggle-hidden]`).each((_,element)=>{let target=$(element);let toggle=$(`#${target.attr("toggle-hidden")}`);if(toggle.attr("value")==="true"){target.show()}else{target.hide()}});toggles.off("click");toggles.on("click",e=>{e.preventDefault();let target=$(e.target);let value=target.attr("value")==="true";target.attr("value",!value);target.trigger("toggle",[{value:!value}]);$(`[toggle-hidden="${target.attr("id")}"]`).toggle()})});